import postReducer from "./post-reducer";
import messageReducer from "./message-reducer";
import dialogsReducer from "./dialogs-reducer";

const GET_STATE = 'GET-STATE';
const ADD_DIALOG = 'ADD-DIALOG';

const ADD_MESSAGE = 'ADD-MESSAGE';
const UPDATE_TEXT_MESSAGE = 'UPDATE-TEXT-MESSAGE';

const ADD_POST = 'ADD-POST';
const UPDATE_TEXT_POST = 'UPDATE-TEXT-POST';


let store = {

    _state :{
        dialogsData : [
            {name : 'Archi', id:1},
            {name : 'Roma', id:2},
            {name : 'Lex', id:3}
        ],
        contentDataPosts: [
            {post : 'Hello!'},
            {post :'Everybody!'},
            {post: 'Vyshe Ruki!'},
            {post : 'Suki Blyadi!'},
            {newPostText: 'Type new Post!'}
        ],
        messagesData: [
            {message: 'Hello!'},
            {message: 'Salut!'},
            {message: 'Ty zdes!'},
            {newMessageText: 'Type new message!'}
        ],
        navData:[
            {name: 'Dialog', url:'/dialogs'},
            {name: 'Profile', url: '/profile'},
            {name: 'Friends', url: '/friends'},
            {name: 'Competitions', url: '/competitions'},

        ]
    },
    _subscriber(){

    },
    subscribe(observer){
        this._subscriber=observer;
    },
    dispatch(action){
        this._state.contentDataPosts = postReducer(this._state.contentDataPosts, action);
        this._state.messagesData = messageReducer(this._state.messagesData, action);
        this._state.dialogsData = dialogsReducer(this._state.dialogsData, action);
        if (action.type === GET_STATE){
            return(this._state);
        }
        this._subscriber(this._state);
    }
}

export let addPostAction = (text) => {
        return(
            {type: ADD_POST, text:text}
        )
}
export let addMessageAction = (text) => {
        return(
            {type: ADD_MESSAGE, text:text}
        )
}
export let addDialogAction = (text) => {
    return(
        {type: ADD_DIALOG, text:text}
    )
}
export let updateTextPostAction = (text) => {
    return(
        {type: UPDATE_TEXT_POST, text:text}
    )
}
export let updateTextMessageAction = (text) => {
    return(
        {type: UPDATE_TEXT_MESSAGE, text:text}
    )
}
export let getStateAction= () => {
    return({type: GET_STATE})
}
export default store;
