import s from './Messages.module.css'
import Message from './Message/Message'
import {getStateAction} from '../../../redux/state'
import React from "react";


const Messages =  (props) => {

    let MessageDataMap = props.store.dispatch(getStateAction()).messagesData.map( (el)=>{
            return(
                <Message message = {el.message}/>
            )
        }

    )
    let newMessageElement  =   React.createRef();
    return (
        <div>
            <div className={s.messages}>
                {MessageDataMap}
            </div>
            <div>
                <textarea onChange={onMessageChange} ref = {newMessageElement} value={props.store.dispatch(getStateAction()).newMessageText}></textarea>
            </div>
        </div>
    )
}
export default Messages;
