import s from "./MyPosts.module.css";
import Post from './Post/Post';
import React from "react";
import Form, {reduxForm} from "redux-form/lib/Form";
import {Field} from "redux-form";


const addPostForm =(props) => {
    return(
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field component={'textarea'} name={'newPostBody'} placeholder={'New post'}></Field>
            </div>
            <div>
                <button className={s.button}>Add post</button>
            </div>
        </form>
    )
}

const MyPosts = (props) => {
    let contMap =  props.postsData.map( (el)=>{
        return(
            <Post message = {el.post}/>
        )
    });
    let newPostElement  =   React.createRef();
    let addPost = () => {
        let text = newPostElement.current.value;
        props.addPost(text)
    }
    let onPostChange = () => {
        props.onPostChange(newPostElement.current.value);
    }
    return (
        <Form>
            <div>
            <Field component={'textarea'} name={'newPostBody'} placeholder={'New post'}></Field>
            </div>
            <div>
            <button className={s.button}>Add post</button>
            </div>
            <div>
                {contMap}
            </div>
        </Form>
    )
}

export default MyPosts;
