import React from "react";
import {connect} from "react-redux";
import Users from "./Users";
import Preloader from "../common/Preloader/Preloader";
import userAPI from "../../api/api";
import {
    follow, getUserThunkCreator,
    setCurrentPage,
    setToggleIsFetching,
    setToggleIsFProgress,
    setTotalUsersCount,
    setUsers,
    unfollow
} from "../../redux/user-reducer";

class UsersContainer extends React.Component {
    componentDidMount() {
        this.props.setToggleIsFetching(true);
        userAPI.getUsers(this.props.currentPage, this.props.pageSize).then(data => {
            this.props.setToggleIsFetching(false);
            this.props.setUsers(data.items);
            this.props.setTotalUsersCount(data.totalCount)
        })
    };


    onUnfollow = (uID) => {
        this.props.setToggleIsFProgress(true, uID);
        userAPI.toUnFollow(uID).then(data => {
            if(data.resultCode == 0){
                this.props.unfollow(uID)
            }
            this.props.setToggleIsFProgress(false, uID);
        })

    }
    onFollow = (uID) => {
        this.props.setToggleIsFProgress(true, uID);
        userAPI.toFollow(uID).then(data => {
            if(data.resultCode == 0){
                this.props.follow(uID)
            }
            this.props.setToggleIsFProgress(false, uID);
        })

    }

    render() {
        return <>
            {this.props.isFetching ?
            <Preloader/> : null}
            <Users key='Users' {...this.props}
                   onPageChanged={this.onPageChanged}
                   onUnfollow={this.onUnfollow}
                   onFollow={this.onFollow}
            />
        </>
    }
}

let mapStateToProps = (state) => {
    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching,
        followingProgress: state.usersPage.followingProgress
    }
}

export default connect(mapStateToProps, {
    follow,
    unfollow,
    setUsers,
    setCurrentPage,
    setTotalUsersCount,
    setToggleIsFetching,
    setToggleIsFProgress,
    getUserThunkCreator
})(UsersContainer);
