import React from "react";
import styles from "./Users.module.css";
import UserPhoto from "../../assets/Images/UserPhoto.jpeg";
import {NavLink} from "react-router-dom";
import axios from "axios";

let Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount/props.pageSize);
    let pages = [];
    for (let i=1; i <= pagesCount; i++){
        pages.push(i);
    }
    return (
        <div>
            <div className={styles.dropdown}>
                <button onClick="myFunction()" className={styles.dropbtn}>Dropdown</button>
                <div id="myDropdown" className={styles.dropdown.content}>
                    <a href="#">Link 1</a>
                    <a href="#">Link 2</a>
                    <a href="#">Link 3</a>
                </div>
            </div>
            <div>
                {pages.map(el => {
                    return <span className={el === props.currentPage ? styles.selectedPage : styles.Page }
                                 onClick={() => {props.onPageChanged(el)} }> {el} < /span>
                })}
            </div>
            <div>
                {
                    props.users.map(u =>
                        <div key = {u.id}>
                        <span>
                            <div>
                                <NavLink to={'/profile/' + u.id}>
                                <img src={u.photos.small != null ? u.photos.small : UserPhoto} className={styles.ava}/>
                                </NavLink>
                            </div>
                            <div>
                                {u.followed ?
                                    <button onClick={() => {
                                        axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{withCredentials:true, headers: {'API-KEY' : '5f4e0eb4-b44d-40ae-82dd-390855809b59'}}).then(response => {
                                            if(response.data.resultCode == 0){
                                                    props.unfollow(u.id)
                                                }
                                            })
                                    }}>Unfollow</button>
                                    : <button onClick={() => {
                                        axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{},{withCredentials:true, headers: {'API-KEY' : '5f4e0eb4-b44d-40ae-82dd-390855809b59'}}).then(response => {
                                            if(response.data.resultCode == 0){
                                                props.follow(u.id)
                                            }
                                        })
                                        }}>Follow</button>
                                }
                            </div>
                        </span>
                            <span>
                            <div>{u.name}</div><div>{u.status}</div>
                            <div>{"u.location.city"}</div>
                            <div>{"u.location.country"}</div>
                        </span>
                        </div>)
                }
            </div>
            <div>
            {pages.map(el => {
                return <span className={el === props.currentPage ? styles.selectedPage : styles.Page }
                             onClick={() => {props.onPageChanged(el)} }> {el} < /span>
            })}
            </div>
        </div>
    )
}
export default Users;
