import React from "react";
import axios from "axios";
import {connect} from "react-redux";
import {setUserInfo, setUserPhoto} from "../../redux/header-reducer";
import Header from "./Header";

let mapStateToProps = (state) => ({
    userData:{
        id: state.headerPage.userData.id,
        login: state.headerPage.userData.login},
        userPhoto: state.headerPage.userPhoto
})

class HeaderContainer extends React.Component{
    getPhoto = (uId) => (
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile/` + uId).then(response => {
            this.props.setUserPhoto(response.data.data)
            debugger
        })
    )
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`,{withCredentials:true}).then(response => {
            this.props.setUserInfo(response.data.data)
            console.log(response.data)
            debugger
        })
    }
    render(){
        if (this.props.userData.id) {this.getPhoto(this.props.userData.id);}
        return (
            <Header {...this.props} login = {this.props.userData.login} photo = {this.props.userPhoto}/>
        )
    }
}

export default connect(mapStateToProps, {
    setUserInfo,
    setUserPhoto
})(HeaderContainer);
