let state = {
    dialogsData : [
        {name : 'Archi', id:1},
        {name : 'Roma', id:2},
        {name : 'Lex', id:3}
    ],
    contentDataPosts: [
        {post : 'Hello!'},
        {post :'Everybody!'},
        {post: 'Vyshe Ruki!'},
        {post : 'Suki Blyadi!'}
    ]

}

let addPost = (post) => {
    state.contentDataPosts.push(map('post',post))
}
let addDialog = (dialog) => {
    state.dialogsData.push(dialog)
}

export default state
export {addPost, addDialog}
