import {authAPI, profileAPI} from "../api/api";
import {setStatus} from "./profile-reducer";

const LOGIN = 'LOGIN';


let initialState = {
    login: null,
    email: null,
    password: null,
    rememberMe: false
}

const loginReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN :
            return {
                ...state,
                email: action.email,
                password: action.password,
                rememberMe: action.rememberMe
            }
        default:
            return state;
    }
}

export let logIn = (email, password, rememberMe) => {
    return(
        {type: LOGIN, email:email, password: password, rememberMe: rememberMe}
    )
}

export let logInToServer = (email, password, rememberMe) =>(dispatch) => {
    authAPI.login(email, password, rememberMe).then(response => {
        if(response.data.resultCode === 0) {
            dispatch(logIn(email, password, rememberMe))
        }
        else {
            console.log('Invalid authorization!')
        }
    })
}

export default  loginReducer;
