import React from 'react';
import {Field, reduxForm} from "redux-form";

const LoginForm = (props) => {
    debugger
    return (
            <form onSubmit={props.handleSubmit} action="">
                <h1>Login</h1>
                <div>
                    <div><Field component={'input'} name={'login'} placeholder={'login'}/></div>
                    <div><Field component={'input'} name={'password'} placeholder={'password'}/></div>
                    <div><Field component={'input'} name={'rememberMe'} type={'checkbox'}/> Remember me</div>
                </div>
                <div>
                    <button type={"submit"} align={'left'}>Login</button>
                    <button type={"button"} align={'right'}>Log out</button>
                </div>
            </form>
    )
}

const ReduxLoginForm = reduxForm ({
    form: 'loginForm'
})(LoginForm)

const Login = (props) => {
    let onSubmit = (formData) => {
        debugger
        props.logInToServer(formData.login, formData.password, formData.rememberMe);
        console.log(formData)
    }
    return (
        <div>
            <ReduxLoginForm onSubmit={onSubmit}/>
        </div>
    )
}

export default Login;
