let state = {
    dialogsData : [
        {name : 'Archi', id:1},
        {name : 'Roma', id:2},
        {name : 'Lex', id:3}
    ],
    contentDataPosts: [
        {post : 'Hello!'},
        {post :'Everybody!'},
        {post: 'Vyshe Ruki!'},
        {post : 'Suki Blyadi!'}
    ],
    newPostText: 'input New Post!'

}

let store = {
    _subscriber(){

    },
    subscribe(observer){
        this._subscriber=observer;
    },
    getState() {
        return(state);
    },
    addDialog(dialog){
        let newDial = {
            id: 4,
            name : dialog
        }
        state.dialogsData.push(newDial)
    },
    addPost(posta){
        let newPost = {
            post: posta
        }
        state.contentDataPosts.push(newPost);
        this._subscriber(state);
    },
    updateTextPost(text, st){
        state.newPostText = text;
        this._subscriber(st)}
}

export default store;
