import React from 'react'
import styles from './Users.module.css'
import axios from "axios";

let Users = (props) => {
    if (props.users.length ===0){
        axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
            props.setUsers(response.data.items)
        })
    }
    return (
        <div>
            {
                props.users.map(u =>
                    <div key = {u.id}>
                        <span>
                            <div>
                                <img src = {u.photos.small != null ? u.photos.small : "../assets/Images/UserPhoto.jpeg"} className={styles.ava}/>
                            </div>
                            <div>
                                {u.followed ? <button onClick={() => {props.unfollow(u.id)}}>Followed</button> : <button onClick={() => {props.follow(u.id)}}>Unfollowed</button>}
                            </div>
                        </span>
                        <span>
                            <div>{u.name}</div><div>{u.status}</div>
                            <div>{"u.location.city"}</div>
                            <div>{"u.location.country"}</div>
                        </span>
                    </div>)
            }
        </div>
    )
}

export default Users;
