import {rendererer} from "./render";

let state = {
    dialogsData : [
        {name : 'Archi', id:1},
        {name : 'Roma', id:2},
        {name : 'Lex', id:3}
    ],
    contentDataPosts: [
        {post : 'Hello!'},
        {post :'Everybody!'},
        {post: 'Vyshe Ruki!'},
        {post : 'Suki Blyadi!'}
    ],
    newPostText: 'input New Post!'

}

let addPost = (posta) => {
    let newPost = {
        post: posta
    }
    state.contentDataPosts.push(newPost);
    rendererer(state);
}
let addDialog = (dialog) => {
    let newDial = {
        id: 4,
        name : dialog
    }
    state.dialogsData.push(newDial)
}
let updateTextPost = (text) => {
    state.newPostText = text;
    rendererer(state)
}

let subscribe =  (observer) => {
    renderer = observer;
}
export default state
export {addPost, addDialog, updateTextPost}
