const ADD_DIALOG = 'ADD-DIALOG';

const dialogsReducer = (state, action) => {
    switch(action.type){
        case ADD_DIALOG:
        let newDial = {
            id: 4,
            name : action.dialog
        }
        state.dialogsData.push(newDial)
        return state;
    }
}

export default dialogsReducer;
